	/**
	 * 模拟分页请求
	 * @param request
	 * @return
	 */
	@RequestMapping(value = "/pages", produces = "application/json")
	public @ResponseBody Map<String,Object> getDataPages(HttpServletRequest request){
		Map<String,Object> result=new HashMap<>();
		List<PageModel> pages=new ArrayList<>();
		int page=Integer.valueOf(request.getParameter("page"));
		for (int i = (page-1)*10+1; i < page*10+1; i++) {
			PageModel model=new PageModel();
			model.setId(i);
			model.setName("商品"+i);
			model.setDesc("商品描述");
			model.setType("服装");
			model.setDate(DateFormatUtils.format(new Date(), "yyyy-MM-dd HH:mm:ss"));
			model.setLeftImage("http://img4.imgtn.bdimg.com/it/u=2931193132,556071294&fm=27&gp=0.jpg");
			model.setRank(2);
			model.setSaleNum(100);
		    pages.add(model);
		}
		
		result.put("products", pages);
		result.put("msg", "success");
		return result;
	}
	
